// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./ERC721LemonadeV1.sol";
import "./rarible/LibPart.sol";
import "./unique/ICollection.sol";
import "./unique/ICollectionHelpers.sol";

contract LemonadeUniqueCollectionV1 is IMintable {
    address public collection;

    constructor(
        address collectionHelpers,
        string memory name,
        string memory description,
        string memory tokenPrefix
    ) payable {
        ICollectionHelpers collectionHelpers_ = ICollectionHelpers(
            collectionHelpers
        );

        collection = collectionHelpers_.createNFTCollection{value: msg.value}(
            name,
            description,
            tokenPrefix
        );
        collectionHelpers_.makeCollectionERC721MetadataCompatible(
            collection,
            ""
        );

        ICollection collection_ = ICollection(collection);

        collection_.addCollectionAdmin(address(this));
        collection_.changeCollectionOwner(msg.sender);
        collection_.setTokenPropertyPermission(
            ROYALTIES_PROPERTY,
            false,
            true,
            false
        );
    }

    function mintToCaller(string memory tokenURI)
        public
        override
        returns (uint256)
    {
        return ICollection(collection).mintWithTokenURI(msg.sender, tokenURI);
    }

    function mintToCallerWithRoyalty(
        string memory tokenURI,
        LibPart.Part[] memory royalties
    ) public override returns (uint256) {
        uint256 tokenId = mintToCaller(tokenURI);

        ICollection(collection).setProperty(
            tokenId,
            ROYALTIES_PROPERTY,
            abi.encode(royalties)
        );

        return tokenId;
    }
}

contracts/ERC721LemonadeV1.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./rarible/LibPart.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

bytes4 constant ERC2981_INTERFACE_ID = 0x2a55205a;
bytes4 constant RaribleRoyaltiesV2_INTERFACE_ID = 0xcad96cca;

interface IMintable {
    function mintToCaller(string memory tokenURI) external returns (uint256);

    function mintToCallerWithRoyalty(
        string memory tokenURI,
        LibPart.Part[] memory royalties
    ) external returns (uint256);
}

contract ERC721LemonadeV1 is ERC721, Ownable, IMintable {
    using Counters for Counters.Counter;

    Counters.Counter public tokenIdTracker;

    mapping(uint256 => string) public tokenURIs;
    mapping(uint256 => LibPart.Part[]) public royalties;

    constructor(string memory name, string memory symbol)
        ERC721(name, symbol)
    {}

    function mintToCaller(string memory tokenURI_)
        public
        override
        returns (uint256)
    {
        uint256 tokenId = tokenIdTracker.current();

        _mint(_msgSender(), tokenId);
        tokenURIs[tokenId] = tokenURI_;

        tokenIdTracker.increment();

        return tokenId;
    }

    function mintToCallerWithRoyalty(
        string memory tokenURI_,
        LibPart.Part[] memory royalties_
    ) public override returns (uint256) {
        uint256 tokenId = mintToCaller(tokenURI_);

        uint256 length = royalties_.length;
        for (uint256 i; i < length; ) {
            royalties[tokenId].push(royalties_[i]);
            unchecked {
                ++i;
            }
        }

        return tokenId;
    }

    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(
            _exists(tokenId),
            "ERC721Lemonade: URI query for nonexistent token"
        );

        return tokenURIs[tokenId];
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override
        returns (bool)
    {
        return
            interfaceId == ERC2981_INTERFACE_ID ||
            interfaceId == RaribleRoyaltiesV2_INTERFACE_ID ||
            super.supportsInterface(interfaceId);
    }

    function getRaribleV2Royalties(uint256 tokenId)
        public
        view
        returns (LibPart.Part[] memory)
    {
        return royalties[tokenId];
    }

    function royaltyInfo(uint256 tokenId, uint256 price)
        public
        view
        returns (address receiver, uint256 royaltyAmount)
    {
        uint256 length = royalties[tokenId].length;

        if (length == 0) {
            return (address(0), 0);
        }

        uint256 totalValue;
        for (uint256 i; i < length; i++) {
            totalValue += royalties[tokenId][i].value;
        }
        return (royalties[tokenId][0].account, (price * totalValue) / 10000);
    }
}

contracts/LemonadeMarketplaceV2.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./IERC2981.sol";
import "./rarible/RoyaltiesV2.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

contract LemonadeMarketplaceV2 is AccessControlEnumerable {
    using Counters for Counters.Counter;

    address public immutable feeAccount;
    uint96 public immutable feeValue;

    enum OrderKind {
        Direct,
        Auction
    }

    event OrderCreated(
        uint256 indexed orderId,
        OrderKind kind,
        uint256 openFrom,
        uint256 openTo,
        address indexed maker,
        address currency,
        uint256 price,
        address tokenContract,
        uint256 tokenId
    );
    event OrderBid(
        uint256 indexed orderId,
        address indexed bidder,
        uint256 bidAmount
    );
    event OrderFilled(
        uint256 indexed orderId,
        address indexed taker,
        uint256 paidAmount
    );
    event OrderCancelled(uint256 indexed orderId);

    struct Order {
        OrderKind kind;
        bool open;
        uint256 openFrom;
        uint256 openTo;
        address maker;
        address currency;
        uint256 price;
        address tokenContract;
        uint256 tokenId;
        address bidder;
        uint256 bidAmount;
        address taker;
        uint256 paidAmount;
    }
    mapping(uint256 => Order) private _orders;
    Counters.Counter public orderIdTracker;

    constructor(address feeAccount_, uint96 feeValue_) {
        feeAccount = feeAccount_;
        feeValue = feeValue_;

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    function order(uint256 orderId)
        external
        view
        virtual
        whenExists(orderId)
        returns (
            OrderKind,
            bool,
            uint256,
            uint256,
            address,
            address,
            uint256,
            uint256,
            address,
            uint256
        )
    {
        Order memory order_ = _orders[orderId];
        return (
            order_.kind,
            order_.open,
            order_.openFrom,
            order_.openTo,
            order_.maker,
            order_.currency,
            order_.price,
            order_.bidAmount,
            order_.taker,
            order_.paidAmount
        );
    }

    function token(uint256 orderId)
        external
        view
        virtual
        whenExists(orderId)
        returns (address, uint256)
    {
        return (_orders[orderId].tokenContract, _orders[orderId].tokenId);
    }

    function createOrder(
        OrderKind kind,
        uint256 openFrom,
        uint256 openTo,
        address currency,
        uint256 price,
        address tokenContract,
        uint256 tokenId
    ) external virtual returns (uint256) {
        uint256 openDuration_ = openDuration(openFrom, openTo);

        require(
            openDuration_ > 0,
            "LemonadeMarketplace: order must be open at some point"
        );

        if (kind == OrderKind.Auction) {
            require(
                openDuration_ <= 30 * 24 * 60 * 60,
                "LemonadeMarketplace: order of kind auction must not be open for more than 30 days"
            );
        }

        IERC721(tokenContract).transferFrom(
            _msgSender(),
            address(this),
            tokenId
        );

        uint256 orderId = orderIdTracker.current();

        _orders[orderId] = Order({
            kind: kind,
            open: true,
            openFrom: openFrom,
            openTo: openTo,
            maker: _msgSender(),
            currency: currency,
            price: price,
            tokenContract: tokenContract,
            tokenId: tokenId,
            bidder: address(0),
            bidAmount: 0,
            taker: address(0),
            paidAmount: 0
        });

        orderIdTracker.increment();

        Order memory order_ = _orders[orderId];
        emit OrderCreated(
            orderId,
            order_.kind,
            order_.openFrom,
            order_.openTo,
            order_.maker,
            order_.currency,
            order_.price,
            order_.tokenContract,
            order_.tokenId
        );
        return orderId;
    }

    function cancelOrder(uint256 orderId) external virtual whenExists(orderId) {
        Order memory order_ = _orders[orderId];
        require(
            order_.maker == _msgSender() ||
                hasRole(DEFAULT_ADMIN_ROLE, _msgSender()),
            "LemonadeMarketplace: must be the maker to cancel"
        );
        require(
            order_.open,
            "LemonadeMarketplace: order must be open to cancel"
        );
        require(
            order_.bidder == address(0),
            "LemonadeMarketplace: order must have no bid to cancel"
        );

        IERC721(order_.tokenContract).transferFrom(
            address(this),
            order_.maker,
            order_.tokenId
        );

        _orders[orderId].open = false;
        emit OrderCancelled(orderId);
    }

    function bidOrder(uint256 orderId, uint256 amount, address subject)
        external
        payable
        virtual
        whenExists(orderId)
    {
        Order memory order_ = _orders[orderId];
        require(
            order_.kind == OrderKind.Auction,
            "LemonadeMarketplace: order must be of kind auction to bid"
        );
        require(order_.open, "LemonadeMarketplace: order must be open to bid");
        require(
            order_.openFrom <= block.timestamp,
            "LemonadeMarketplace: order must be open to bid - too early"
        );
        require(
            order_.openTo > block.timestamp,
            "LemonadeMarketplace: order must be open to bid - too late"
        );
        require(
            order_.price <= amount,
            "LemonadeMarketplace: must match price to bid"
        );
        require(
            order_.currency != address(0) || amount == msg.value,
            "LemonadeMarketplace: amount must match tx value"
        );

        if (order_.bidder != address(0)) {
            require(
                order_.bidAmount < amount,
                "LemonadeMarketplace: must surpass bid to bid"
            );

            if (order_.bidAmount > 0) {
                transfer(
                    order_.currency,
                    address(this),
                    order_.bidder,
                    order_.bidAmount
                );
            }
        }

        address spender = _msgSender();

        _orders[orderId].bidder = subject != address(0) ? subject : spender;
        _orders[orderId].bidAmount = amount;
        order_ = _orders[orderId];

        if (order_.bidAmount > 0) {
            transfer(
                order_.currency,
                spender,
                address(this),
                order_.bidAmount
            );
        }

        emit OrderBid(orderId, order_.bidder, order_.bidAmount);
    }

    function fillOrder(uint256 orderId, uint256 amount, address subject)
        external
        payable
        virtual
        whenExists(orderId)
    {
        Order memory order_ = _orders[orderId];
        require(order_.open, "LemonadeMarketplace: order must be open to fill");

        _orders[orderId].open = false;
        address spender;

        if (order_.kind == OrderKind.Direct) {
            require(
                order_.openFrom <= block.timestamp,
                "LemonadeMarketplace: order must be open to fill - too early"
            );
            require(
                order_.openTo == 0 || order_.openTo > block.timestamp,
                "LemonadeMarketplace: order must be open to fill - too late"
            );
            require(
                order_.price <= amount,
                "LemonadeMarketplace: must match price to fill direct order"
            );
            require(
                order_.currency != address(0) || amount == msg.value,
                "LemonadeMarketplace: amount must match tx value"
            );

            spender = _msgSender();
            _orders[orderId].taker = subject != address(0) ? subject : spender;
            _orders[orderId].paidAmount = amount;
        } else if (order_.kind == OrderKind.Auction) {
            require(
                (order_.bidder != address(0)),
                "LemonadeMarketplace: order must have bid to fill auction order"
            );
            require(
                (order_.bidder == _msgSender() &&
                    order_.openTo <= block.timestamp) ||
                    order_.maker == _msgSender() ||
                    hasRole(DEFAULT_ADMIN_ROLE, _msgSender()),
                "LemonadeMarketplace: must be the maker or final bidder to fill auction order"
            );

            _orders[orderId].taker = order_.bidder;
            _orders[orderId].paidAmount = order_.bidAmount;
            spender = address(this);
        }

        order_ = _orders[orderId];

        if (order_.paidAmount > 0) {
            uint256 transferAmount = order_.paidAmount;

            uint256 feeAmount = (order_.paidAmount * feeValue) / 10000;
            transfer(order_.currency, spender, feeAccount, feeAmount);
            transferAmount -= feeAmount;

            (
                bool supportsRaribleV2Royalties,
                LibPart.Part[] memory royalties
            ) = getRaribleV2Royalties(order_.tokenContract, order_.tokenId);
            if (supportsRaribleV2Royalties) {
                uint256 length = royalties.length;
                for (uint256 i; i < length; i++) {
                    if (
                        order_.maker != royalties[i].account &&
                        royalties[i].value > 0
                    ) {
                        uint256 royaltyAmount = (order_.paidAmount *
                            royalties[i].value) / 10000;
                        transfer(
                            order_.currency,
                            spender,
                            royalties[i].account,
                            royaltyAmount
                        );
                        transferAmount -= royaltyAmount;
                    }
                }
            } else {
                (
                    bool supportsRoyaltyInfo,
                    address receiver,
                    uint256 royaltyAmount
                ) = getRoyaltyInfo(
                        order_.tokenContract,
                        order_.tokenId,
                        order_.paidAmount
                    );
                if (
                    supportsRoyaltyInfo &&
                    order_.maker != receiver &&
                    royaltyAmount > 0
                ) {
                    transfer(order_.currency, spender, receiver, royaltyAmount);
                    transferAmount -= royaltyAmount;
                }
            }

            if (transferAmount > 0) {
                transfer(
                    order_.currency,
                    spender,
                    order_.maker,
                    transferAmount
                );
                transferAmount -= transferAmount;
            }

            require(
                transferAmount == 0,
                "LemonadeMarketplace: transfer amount must be zero"
            );
        }

        IERC721(order_.tokenContract).transferFrom(
            address(this),
            order_.taker,
            order_.tokenId
        );

        emit OrderFilled(orderId, order_.taker, order_.paidAmount);
    }

    function getRaribleV2Royalties(address tokenContract, uint256 tokenId)
        public
        view
        virtual
        returns (bool, LibPart.Part[] memory)
    {
        try RoyaltiesV2(tokenContract).getRaribleV2Royalties(tokenId) returns (
            LibPart.Part[] memory royalties
        ) {
            return (true, royalties);
        } catch {
            return (false, new LibPart.Part[](0));
        }
    }

    function getRoyaltyInfo(
        address tokenContract,
        uint256 tokenId,
        uint256 paidAmount
    )
        public
        view
        virtual
        returns (
            bool,
            address,
            uint256
        )
    {
        try IERC2981(tokenContract).royaltyInfo(tokenId, paidAmount) returns (
            address receiver,
            uint256 royaltyAmount
        ) {
            return (true, receiver, royaltyAmount);
        } catch {
            return (false, address(0), 0);
        }
    }

    function transfer(
        address currency,
        address spender,
        address recipient,
        uint256 amount
    ) private {
        if (currency == address(0)) {
            if (recipient != address(this)) {
                payable(recipient).transfer(amount);
            }
        } else {
            if (spender == address(this)) {
                IERC20(currency).transfer(recipient, amount);
            } else {
                IERC20(currency).transferFrom(spender, recipient, amount);
            }
        }
    }

    function openDuration(uint256 openFrom, uint256 openTo)
        private
        view
        returns (uint256)
    {
        uint256 start = openFrom < block.timestamp ? block.timestamp : openFrom;
        uint256 end = openTo == 0 ? type(uint256).max : openTo;

        if (start > end) {
            return 0;
        }
        return end - start;
    }

    modifier whenExists(uint256 orderId) {
        require(
            _orders[orderId].maker != address(0),
            "LemonadeMarketplace: order nonexistent"
        );
        _;
    }
}

