contracts/ERC721CollectionV1.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;
pragma abicoder v2;

import "./rarible/LibPart.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165.sol";

bytes4 constant ERC2981_INTERFACE_ID = 0x2a55205a;
bytes4 constant RaribleRoyaltiesV2_INTERFACE_ID = 0xcad96cca;

contract OwnableDelegateProxy {}

contract ProxyRegistry {
    mapping(address => OwnableDelegateProxy) public proxies;
}

contract ERC721CollectionV1 is ERC165, ERC721, Ownable {
    address private _proxyRegistryAddress;
    address private _creator;
    uint96 private _royalty;
    string private _baseTokenURI;
    uint256 private _totalSupply;

    constructor(
        string memory name,
        string memory symbol,
        address proxyRegistryAddress,
        address creator,
        uint96 royalty,
        string memory baseTokenURI,
        uint256 totalSupply_
    ) ERC721(name, symbol) {
        _transferOwnership(creator);

        _proxyRegistryAddress = proxyRegistryAddress;
        _creator = creator;
        _royalty = royalty;
        _baseTokenURI = baseTokenURI;
        _totalSupply = totalSupply_;

        for (uint256 tokenId; tokenId < totalSupply_; tokenId++) {
            _mint(creator, tokenId);
        }
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return _baseTokenURI;
    }

    function getRaribleV2Royalties(uint256)
        external
        view
        returns (LibPart.Part[] memory)
    {
        LibPart.Part[] memory result;
        if (_royalty == 0) {
            return result;
        }

        result = new LibPart.Part[](1);
        result[0].account = payable(_creator);
        result[0].value = _royalty;
        return result;
    }

    function isApprovedForAll(address owner, address operator)
        public
        view
        override
        returns (bool)
    {
        // Whitelist OpenSea proxy contract for easy trading.
        ProxyRegistry proxyRegistry = ProxyRegistry(_proxyRegistryAddress);
        if (address(proxyRegistry.proxies(owner)) == operator) {
            return true;
        }

        return super.isApprovedForAll(owner, operator);
    }

    function royaltyInfo(uint256, uint256 price)
        external
        view
        returns (address receiver, uint256 royaltyAmount)
    {
        if (_royalty == 0) {
            return (address(0), 0);
        }

        return (_creator, (price * _royalty) / 10000);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC165, ERC721)
        returns (bool)
    {
        return
            interfaceId == ERC2981_INTERFACE_ID ||
            interfaceId == RaribleRoyaltiesV2_INTERFACE_ID ||
            super.supportsInterface(interfaceId);
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }
}

contracts/ERC721LemonadeV1.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./rarible/LibPart.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

bytes4 constant ERC2981_INTERFACE_ID = 0x2a55205a;
bytes4 constant RaribleRoyaltiesV2_INTERFACE_ID = 0xcad96cca;

interface IMintable {
    function mintToCaller(string memory tokenURI) external returns (uint256);

    function mintToCallerWithRoyalty(
        string memory tokenURI,
        LibPart.Part[] memory royalties
    ) external returns (uint256);
}

contract ERC721LemonadeV1 is ERC721, Ownable, IMintable {
    using Counters for Counters.Counter;

    Counters.Counter public tokenIdTracker;

    mapping(uint256 => string) public tokenURIs;
    mapping(uint256 => LibPart.Part[]) public royalties;

    constructor(string memory name, string memory symbol)
        ERC721(name, symbol)
    {}

    function mintToCaller(string memory tokenURI_)
        public
        override
        returns (uint256)
    {
        uint256 tokenId = tokenIdTracker.current();

        _mint(_msgSender(), tokenId);
        tokenURIs[tokenId] = tokenURI_;

        tokenIdTracker.increment();

        return tokenId;
    }

    function mintToCallerWithRoyalty(
        string memory tokenURI_,
        LibPart.Part[] memory royalties_
    ) public override returns (uint256) {
        uint256 tokenId = mintToCaller(tokenURI_);

        uint256 length = royalties_.length;
        for (uint256 i; i < length; ) {
            royalties[tokenId].push(royalties_[i]);
            unchecked {
                ++i;
            }
        }

        return tokenId;
    }

    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(
            _exists(tokenId),
            "ERC721Lemonade: URI query for nonexistent token"
        );

        return tokenURIs[tokenId];
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override
        returns (bool)
    {
        return
            interfaceId == ERC2981_INTERFACE_ID ||
            interfaceId == RaribleRoyaltiesV2_INTERFACE_ID ||
            super.supportsInterface(interfaceId);
    }

    function getRaribleV2Royalties(uint256 tokenId)
        public
        view
        returns (LibPart.Part[] memory)
    {
        return royalties[tokenId];
    }

    function royaltyInfo(uint256 tokenId, uint256 price)
        public
        view
        returns (address receiver, uint256 royaltyAmount)
    {
        uint256 length = royalties[tokenId].length;

        if (length == 0) {
            return (address(0), 0);
        }

        uint256 totalValue;
        for (uint256 i; i < length; i++) {
            totalValue += royalties[tokenId][i].value;
        }
        return (royalties[tokenId][0].account, (price * totalValue) / 10000);
    }
}

