        ICollection(collection).setProperties(tokenId, properties);

        return tokenId;
    }
}

contracts/passport/BaseV1.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./GatewayV1Axelar.sol";
import "./GatewayV1Call.sol";
import "./IBaseV1.sol";
import "./Shared.sol";

contract BaseV1 is GatewayV1Axelar, GatewayV1Call, IBaseV1 {
    bytes32 public callNetwork;
    uint256 public maxSupply;

    uint256 private _tokenIdCounter;
    uint256 private _totalReservations;

    mapping(address => uint256) private _referrals;
    mapping(address => uint256) private _reservations;
    mapping(address => uint256) private _tokens;

    mapping(uint256 => address) private _owners;
    mapping(uint256 => bytes32) private _networks;

    function initialize(
        IAxelarGateway axelarGateway,
        IAxelarGasService axelarGasService,
        AxelarNetwork[] calldata axelarNetworks,
        address callAddress,
        bytes32 callNetwork_,
        uint256 maxSupply_
    ) public initializer {
        __GatewayV1Axelar_init(axelarGateway, axelarGasService, axelarNetworks);
        __GatewayV1Call_init(callAddress);

        callNetwork = callNetwork_;
        maxSupply = maxSupply_;

        _tokenIdCounter = 1;

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    function assign(Assignment[] calldata assignments) public override {
        address sender = _msgSender();

        _assign(sender, assignments);

        emit Assign(sender, assignments);
    }

    function claim(bytes32 network) public payable override {
        address sender = _msgSender();

        uint256 tokenId = _claim(network, sender, msg.value, sender);

        emit Claim(sender, network, tokenId);
    }

    function grant(
        Assignment[] calldata assignments
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        (bool success, ) = _tryReserve(assignments);

        if (!success) {
            revert Forbidden();
        }
    }

    function balanceOf(
        address owner
    ) public view override returns (uint256 balance) {
        if (_tokens[owner] != 0) {
            balance = 1;
        }
    }

    function networkOf(uint256 tokenId) public view override returns (bytes32) {
        if (_owners[tokenId] == address(0)) {
            revert NotFound();
        }

        return _networks[tokenId];
    }

    function ownerOf(
        uint256 tokenId
    ) public view override returns (address owner) {
        owner = _owners[tokenId];

        if (owner == address(0)) {
            revert NotFound();
        }
    }

    function referrals(
        address referrer
    ) public view override returns (uint256) {
        return _referrals[referrer];
    }

    function reservations(
        address owner
    ) public view override returns (uint256) {
        return _reservations[owner];
    }

    function token(address owner) public view override returns (uint256) {
        return tokenOfOwnerByIndex(owner, 0);
    }

    function tokenByIndex(
        uint256 index
    ) public view override returns (uint256) {
        if (index >= totalSupply()) {
            revert NotFound();
        }

        return index + 1;
    }

    function tokenOfOwnerByIndex(
        address owner,
        uint256 index
    ) public view override returns (uint256 tokenId) {
        tokenId = _tokens[owner];

        if (tokenId == 0 || index > 0) {
            revert NotFound();
        }
    }

    function totalReservations() public view override returns (uint256) {
        return _totalReservations;
    }

    function totalSupply() public view override returns (uint256) {
        unchecked {
            return _tokenIdCounter - 1;
        }
    }

    function _assign(address sender, Assignment[] memory assignments) internal {
        uint256 count = _increaseReservations(assignments);

        if (!_tryReservationsDecrease(sender, count)) {
            revert Forbidden();
        }
    }

    function _callContract(
        bytes32 network,
        bytes32 method,
        bytes memory params,
        uint256 gasFee,
        address refundAddress
    ) internal override {
        if (network == callNetwork) {
            return GatewayV1Call._callContract(method, params);
        }

        GatewayV1Axelar._callContract(
            network,
            method,
            params,
            gasFee,
            refundAddress
        );
    }

    function _claim(
        bytes32 network,
        address sender,
        uint256 gasFee,
        address refundAddress
    ) internal returns (uint256 tokenId) {
        tokenId = _tokenIdCounter;

        if (
            !_tryReservationsDecrease(sender, 1) ||
            !_tryMint(network, sender, tokenId)
        ) {
            revert Forbidden();
        }

        unchecked {
            _totalReservations--;
            _tokenIdCounter = tokenId + 1;
        }

        _callContract(
            network,
            CLAIM_METHOD,
            abi.encode(sender, tokenId),
            gasFee,
            refundAddress
        );
    }

    function _execute(bytes32 method, bytes memory params) internal override {
        _execute(callNetwork, method, params);
    }

    function _execute(
        bytes32 network,
        bytes32 method,
        bytes memory params
    ) internal override {
        if (method == ASSIGN_METHOD) {
            _executeAssign(network, params);
        } else if (method == CLAIM_METHOD) {
            _executeClaim(network, params);
        } else if (method == PURCHASE_METHOD) {
            _executePurchase(network, params);
        } else if (method == RESERVE_METHOD) {
            _executeReserve(network, params);
        } else {
            revert NotImplemented();
        }
    }

    function _executeAssign(bytes32 network, bytes memory params) internal {
        (address sender, Assignment[] memory assignments) = abi.decode(
            params,
            (address, Assignment[])
        );

        _assign(sender, assignments);

        emit ExecuteAssign(network, sender, assignments);
    }

    function _executeClaim(bytes32 network, bytes memory params) internal {
        address sender = abi.decode(params, (address));

        uint256 tokenId = _claim(network, sender, 0, address(0));

        emit ExecuteClaim(network, sender, tokenId);
    }

    function _executePurchase(bytes32 network, bytes memory params) internal {
        (uint256 purchaseId, address sender, address referrer) = abi.decode(
            params,
            (uint256, address, address)
        );

        bool success;
        uint256 tokenId = _tokenIdCounter;

        if (
            _totalReservations + tokenId <= maxSupply &&
            _tryMint(network, sender, tokenId)
        ) {
            success = true;

            unchecked {
                _tokenIdCounter = tokenId + 1;
            }
        }

        if (_tokens[referrer] == 0) {
            delete referrer;
        } else if (success) {
            unchecked {
                ++_referrals[referrer];
            }
        }

        emit ExecutePurchase(
            network,
            purchaseId,
            sender,
            referrer,
            tokenId,
            success
        );

        _callContract(
            network,
            PURCHASE_METHOD,
            abi.encode(purchaseId, referrer, tokenId, success),
            0,
            address(0)
        );
    }

    function _executeReserve(bytes32 network, bytes memory params) internal {
        (
            uint256 paymentId,
            address sender,
            Assignment[] memory assignments
        ) = abi.decode(params, (uint256, address, Assignment[]));

        (bool success, uint256 count) = _tryReserve(assignments);

        if (success) {
            _referrals[sender] += count;
        }

        bool referred = _tokens[sender] != 0;

        emit ExecuteReserve(
            network,
            paymentId,
            sender,
            assignments,
            referred,
            success
        );

        _callContract(
            network,
            RESERVE_METHOD,
            abi.encode(paymentId, referred, success),
            0,
            address(0)
        );
    }

    function _increaseReservations(
        Assignment[] memory assignments
    ) internal returns (uint256 count) {
        uint256 length = assignments.length;

        for (uint256 i; i < length; ) {
            Assignment memory assignment = assignments[i];

            _reservations[assignment.to] += assignment.count;

            count += assignment.count;

            unchecked {
                ++i;
            }
        }
    }

    function _tryMint(
        bytes32 network,
        address to,
        uint256 tokenId
    ) internal returns (bool success) {
        if (_tokens[to] != 0) {
            return false;
        }

        _tokens[to] = tokenId;
        _owners[tokenId] = to;
        _networks[tokenId] = network;

        emit Mint(network, to, tokenId);

        return true;
    }

    function _tryReservationsDecrease(
        address owner,
        uint256 count
    ) internal returns (bool success) {
        uint256 reservations_ = _reservations[owner];

        if (reservations_ < count) {
            return false;
        }

        unchecked {
            _reservations[owner] = reservations_ - count;
        }

        return true;
    }

    function _tryReserve(
        Assignment[] memory assignments
    ) internal returns (bool success, uint256) {
        uint256 totalReservations_ = _totalReservations +
            countAssignments(assignments);

        if (totalReservations_ + totalSupply() > maxSupply) {
            return (false, 0);
        }

        _totalReservations = totalReservations_;

        return (true, _increaseReservations(assignments));
    }
}

contracts/passport/CrowdfundV1.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./ICrowdfundV1.sol";
import "./IPassportV1.sol";
import "./IPassportV1Reserver.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PullPaymentUpgradeable.sol";

contract CrowdfundV1 is
    AccessControlUpgradeable,
    PullPaymentUpgradeable,
    ICrowdfundV1,
    IPassportV1Reserver
{
    IPassportV1 public passport;

    uint256 private _campaignIdCounter;
    struct Campaign {
        State state;
        address creator;
        string title;
        string description;
        address[] contributors;
        uint256 total;
        uint256 unused;
    }
    mapping(uint256 => Campaign) private _campaigns;
    mapping(uint256 => Assignment[]) private _assignments;
    mapping(uint256 => mapping(address => uint256)) private _contributions;

    function initialize(IPassportV1 passport_) public initializer {
        __PullPayment_init();

        passport = passport_;

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    receive() external payable {}

    function create(
        string memory title_,
        string memory description_,
        Assignment[] memory assignments_
    ) public override returns (uint256 campaignId) {
        unchecked {
            campaignId = _campaignIdCounter++;
        }

        address creator_ = _msgSender();

        _campaigns[campaignId] = Campaign({
            state: State.PENDING,
            creator: creator_,
            title: title_,
            description: description_,
            contributors: new address[](0),
            total: 0,
            unused: 0
        });

        uint256 length = assignments_.length;

        for (uint256 i; i < length; ) {
            _assignments[campaignId].push(assignments_[i]);

            unchecked {
                ++i;
            }
        }

        emit Create(creator_, title_, description_, assignments_, campaignId);
    }

    function execute(
        uint256 campaignId,
        uint160 roundIds
    ) public payable override whenExists(campaignId) {
        Assignment[] memory assignments_ = _assignments[campaignId];

        uint256 price = countAssignments(assignments_) *
            passport.priceAt(roundIds);

        Campaign memory campaign = _campaigns[campaignId];

        if (campaign.state != State.PENDING || campaign.total < price) {
            revert Forbidden();
        }

        _setState(campaignId, State.EXECUTED);

        unchecked {
            _campaigns[campaignId].unused = campaign.total - price;
        }

        passport.reserve{value: price + msg.value}(
            roundIds,
            assignments_,
            abi.encode(campaignId)
        );
    }

    function fund(
        uint256 campaignId
    ) public payable override whenExists(campaignId) {
        Campaign memory campaign = _campaigns[campaignId];

        if (campaign.state != State.PENDING || msg.value == 0) {
            revert Forbidden();
        }

        address contributor = _msgSender();
        uint256 contribution = _contributions[campaignId][contributor];

        if (contribution == 0) {
            _campaigns[campaignId].contributors.push(contributor);
        }

        _campaigns[campaignId].total = campaign.total + msg.value;
        _contributions[campaignId][contributor] = contribution + msg.value;

        emit Fund(campaignId, contributor, msg.value);
    }

    function onPassportV1Reserved(
        bool success,
        bytes calldata data
    ) public override {
        uint256 campaignId = abi.decode(data, (uint256));

        Campaign memory campaign = _campaigns[campaignId];

        if (
            campaign.state != State.EXECUTED ||
            _msgSender() != address(passport)
        ) {
            revert Forbidden();
        }

        if (success) {
            _setState(campaignId, State.CONFIRMED);

            if (campaign.unused > 0) {
                _asyncTransfer(campaign.creator, campaign.unused);
            }
        } else {
            _refund(campaignId);
        }
    }

    function refund(uint256 campaignId) public override whenExists(campaignId) {
        Campaign memory campaign = _campaigns[campaignId];

        if (
            campaign.state != State.PENDING || _msgSender() != campaign.creator
        ) {
            revert Forbidden();
        }

        _refund(campaignId);
    }

    function withdraw(
        address recipient,
        uint256 amount
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _asyncTransfer(recipient, amount);
    }

    function assignments(
        uint256 campaignId
    )
        public
        view
        override
        whenExists(campaignId)
        returns (Assignment[] memory)
    {
        return _assignments[campaignId];
    }

    function contributors(
        uint256 campaignId
    ) public view override whenExists(campaignId) returns (address[] memory) {
        return _campaigns[campaignId].contributors;
    }

    function creator(
        uint256 campaignId
    ) public view override whenExists(campaignId) returns (address) {
        return _campaigns[campaignId].creator;
    }

    function description(
        uint256 campaignId
    ) public view override whenExists(campaignId) returns (string memory) {
        return _campaigns[campaignId].description;
    }

    function goal(
        uint256 campaignId
    )
        public
        view
        override
        whenExists(campaignId)
        returns (uint160, uint256 amount)
    {
        (uint160 roundIds, uint256 price) = passport.price();

        return (roundIds, countAssignments(_assignments[campaignId]) * price);
    }

    function state(
        uint256 campaignId
    ) public view override whenExists(campaignId) returns (State) {
        return _campaigns[campaignId].state;
    }

    function title(
        uint256 campaignId
    ) public view override whenExists(campaignId) returns (string memory) {
        return _campaigns[campaignId].title;
    }

    function total(
        uint256 campaignId
    ) public view override whenExists(campaignId) returns (uint256) {
        return _campaigns[campaignId].total;
    }

    function _refund(uint256 campaignId) internal {
        _setState(campaignId, State.REFUNDED);

        address[] memory contributors_ = _campaigns[campaignId].contributors;
        uint256 length = contributors_.length;

        for (uint256 i; i < length; ) {
            address contributor = contributors_[i];

            _asyncTransfer(
                contributor,
                _contributions[campaignId][contributor]
            );

            unchecked {
                ++i;
            }
        }
    }

    function _setState(uint256 campaignId, State state_) internal {
        _campaigns[campaignId].state = state_;

        emit StateChanged(campaignId, state_);
    }

    modifier whenExists(uint256 campaignId) {
        if (_campaigns[campaignId].creator == address(0)) {
            revert NotFound();
        }

        _;
    }
}

