contracts/GenericFactory.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

contract CounterfactualFactory {
    function _create2(
        bytes memory _code,
        bytes32 _salt
    ) internal returns (address) {
        bytes memory code = _code;
        bytes32 salt = _salt;
        address addr;
        assembly {
            addr := create2(0, add(code, 0x20), mload(code), salt)
            if iszero(extcodesize(addr)) {
                revert(0, 0)
            }
        }
        return addr;
    }

    function _predictAddress(
        bytes memory _code,
        bytes32 _salt
    ) internal view returns (address) {
        return
            address(
                bytes20(
                    keccak256(
                        abi.encodePacked(
                            bytes1(0xff),
                            address(this),
                            _salt,
                            keccak256(_code)
                        )
                    ) << 0x60
                )
            );
    }
}

contract GenericFactory is CounterfactualFactory {
    event NewContract(address indexed addr);

    function predictAddress(
        bytes memory _code,
        bytes32 _salt
    ) public view returns (address) {
        return predictAddressWithCall(_code, _salt, bytes(""));
    }

    function createContract(
        bytes memory _code,
        bytes32 _salt
    ) public returns (address) {
        return createContractAndCall(_code, _salt, bytes(""));
    }

    function predictAddressWithCall(
        bytes memory _code,
        bytes32 _salt,
        bytes memory _call
    ) public view returns (address) {
        return
            _predictAddress(_code, keccak256(abi.encodePacked(_salt, _call)));
    }

    function createContractAndCall(
        bytes memory _code,
        bytes32 _salt,
        bytes memory _call
    ) public returns (address) {
        address addr = _create2(
            _code,
            keccak256(abi.encodePacked(_salt, _call))
        );
        emit NewContract(addr);
        if (_call.length > 0) {
            (bool success, bytes memory reason) = addr.call(_call);
            require(success, string(reason));
        }
        return addr;
    }
}

contracts/payment/PaymentConfigRegistry..content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "../AccessRegistry.sol";

bytes32 constant PAYMENT_ADMIN_ROLE = keccak256("PAYMENT_ADMIN_ROLE");

contract PaymentConfigRegistry is OwnableUpgradeable {
    using ECDSA for bytes;
    using ECDSA for bytes32;

    error InvalidSignature();
    error Forbidden();
    error CannotWithdraw();

    address public accessRegistry;
    address public authorizedSigner;
    uint256 public feePPM;
    uint256[20] __gap;

    event FeeCollected(string eventId, address token, uint256 amount);

    function initialize(
        address registry,
        address signer,
        uint256 ppm
    ) public initializer {
        __Ownable_init();
        accessRegistry = registry;
        authorizedSigner = signer;
        feePPM = ppm;
    }

    function setAccessRegistry(address registry) external onlyOwner {
        accessRegistry = registry;
    }

    function setAuthorizedSigner(address signer) external onlyAdmin {
        authorizedSigner = signer;
    }

    function setFeePPM(uint256 ppm) external onlyAdmin {
        feePPM = ppm;
    }

    function withdraw(
        address token,
        uint256 amount,
        address payable destination
    ) external onlyAdmin {
        bool success;

        if (token == address(0)) {
            (success, ) = destination.call{value: amount}("");
        } else {
            success = IERC20(token).transfer(destination, amount);
        }

        if (!success) revert CannotWithdraw();
    }

    function notifyFee(
        string calldata eventId,
        address token,
        uint256 amount
    ) external {
        emit FeeCollected(eventId, token, amount);
    }

    function assertSignature(
        bytes32[] calldata data,
        bytes calldata signature
    ) public view {
        bytes memory encoded;
        uint256 length = data.length;

        for (uint256 i = 0; i < length; ) {
            encoded = abi.encodePacked(encoded, data[i]);

            unchecked {
                ++i;
            }
        }

        address actualSigner = encoded.toEthSignedMessageHash().recover(
            signature
        );

        if (actualSigner != authorizedSigner) {
            revert InvalidSignature();
        }
    }

    function balances(
        address[] calldata currencies
    ) external view returns (uint256[] memory balance_) {
        uint256 length = currencies.length;

        balance_ = new uint256[](length);

        address contractAddress = address(this);

        for (uint256 i = 0; i < length; ) {
            address currency = currencies[i];

            if (currency == address(0)) {
                balance_[i] = contractAddress.balance;
            } else {
                balance_[i] = IERC20(currency).balanceOf(contractAddress);
            }

            unchecked {
                ++i;
            }
        }

        return balance_;
    }

    receive() external payable {}

    fallback() external payable {}

    modifier onlyAdmin() {
        if (
            !AccessRegistry(accessRegistry).hasRole(
                PAYMENT_ADMIN_ROLE,
                _msgSender()
            )
        ) {
            revert Forbidden();
        }
        _;
    }
}

