// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "../PaymentConfigRegistry.sol";
import "../PaymentSplitter.sol";

contract RelayPaymentSplitter is PaymentSplitter {
    address internal _owner;

    error Forbidden();

    constructor(
        address owner,
        address[] memory payees,
        uint256[] memory shares
    ) PaymentSplitter(payees, shares) {
        _owner = owner;
    }

    function resetPayees(
        address[] memory payees,
        uint256[] memory shares
    ) public {
        if (_msgSender() != _owner) revert Forbidden();

        _resetPayees(payees, shares);
    }
}

contract LemonadeRelayPayment is OwnableUpgradeable {
    struct Payment {
        address guest;
        address currency;
        uint256 amount;
    }

    address public configRegistry;
    mapping(bytes32 => Payment) internal payments;
    mapping(address => bool) internal splitters;
    uint256[20] __gap;

    event OnRegister(address splitter);

    event OnPay(
        address splitter,
        string eventId,
        string paymentId,
        address guest,
        address currency,
        uint256 amount
    );

    error NotRegistered();
    error AlreadyPay();
    error InvalidAmount();
    error CannotPayFee();
    error CannotPay();

    function initialize(address registry) public initializer {
        __Ownable_init();
        configRegistry = registry;
    }

    function setConfigRegistry(address registry) external onlyOwner {
        configRegistry = registry;
    }

    /**
     * Register wallets to receive payments of a specific event
     * @param payees Param of payment splitter
     * @param shares Param of payment splitter
     */
    function register(
        address[] calldata payees,
        uint256[] calldata shares
    ) external {
        RelayPaymentSplitter splitter = new RelayPaymentSplitter(
            _msgSender(),
            payees,
            shares
        );

        address splitterAddress = address(splitter);

        splitters[splitterAddress] = true;

        emit OnRegister(splitterAddress);
    }

    function getPayment(
        string calldata paymentId
    ) external view returns (Payment memory) {
        bytes32 id = _toId(paymentId);

        return payments[id];
    }

    /**
     * Guest pays the tickets
     * @param splitter The address of the registered splitter
     * @param paymentId The id of the payment
     * @param currency Token address of the currency, zero address for native currency
     * @param amount The ticket amount plus fee
     */
    function pay(
        address splitter,
        string memory eventId,
        string memory paymentId,
        address currency,
        uint256 amount
    ) external payable {
        if (amount == 0) revert InvalidAmount();

        bytes32 id = _toId(paymentId);

        if (payments[id].amount > 0) revert AlreadyPay();

        if (!splitters[splitter]) revert NotRegistered();

        bool isNative = currency == address(0);

        if (isNative && msg.value != amount) revert InvalidAmount();

        PaymentConfigRegistry registry = PaymentConfigRegistry(
            payable(configRegistry)
        );

        uint256 transferAmount = amount * 1000000 / (registry.feePPM() + 1000000);
        uint256 feeAmount = amount - transferAmount;

        address guest = _msgSender();

        if (isNative) {
            (bool success, ) = payable(configRegistry).call{value: feeAmount}(
                ""
            );

            if (!success) revert CannotPayFee();

            (success, ) = payable(splitter).call{value: transferAmount}("");

            if (!success) revert CannotPay();
        } else {
            bool success = IERC20(currency).transferFrom(
                guest,
                configRegistry,
                feeAmount
            );

            if (!success) revert CannotPayFee();

            success = IERC20(currency).transferFrom(
                guest,
                splitter,
                transferAmount
            );

            if (!success) revert CannotPay();
        }

        registry.notifyFee(eventId, currency, feeAmount);

        Payment memory payment = Payment(guest, currency, amount);

        payments[id] = payment;

        emit OnPay(
            splitter,
            eventId,
            paymentId,
            guest,
            currency,
            transferAmount
        );
    }

    function _toId(string memory id) internal pure returns (bytes32) {
        return keccak256(abi.encode(id));
    }
}

contracts/payment/stake/LemonadeStakePay.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/utils/Create2.sol";

import "../../utils/Data.sol";
import "../PaymentConfigRegistry.sol";
import "./StakeVault.sol";

bytes32 constant STAKE_REFUND = keccak256(abi.encode("STAKE_REFUND"));
bytes32 constant STAKE_SLASH = keccak256(abi.encode("STAKE_SLASH"));

contract LemonadeStakePayment is OwnableUpgradeable, Transferable {
    //-- STORAGE
    address public configRegistry;
    mapping(bytes32 => address) public stakings; //-- key is payment id
    uint256[5] __gap;

    //-- ERRORS
    error InvalidData();

    //-- EVENTS
    event VaultRegistered(address vault);

    function initialize(address registry) public initializer {
        __Ownable_init();
        configRegistry = registry;
    }

    function setConfigRegistry(address registry) external onlyOwner {
        configRegistry = registry;
    }

    function register(
        bytes32 salt,
        address payout,
        uint256 refundPPM
    ) external {
        address owner = _msgSender();

        bytes memory bytecode = abi.encodePacked(
            type(StakeVault).creationCode,
            abi.encode(owner)
        );

        address vault = Create2.deploy(0, salt, bytecode);

        StakeVault stakeVault = StakeVault(payable(vault));
        stakeVault.initialize(payout, refundPPM);

        emit VaultRegistered(vault);
    }

    function stake(
        address vault,
        string calldata eventId,
        string calldata paymentId,
        address currency,
        uint256 amount
    ) external payable {
        if (amount == 0) {
            revert InvalidData();
        }

        bytes32 stakeId = stringToId(paymentId);

        if (stakings[stakeId] != address(0)) {
            revert AlreadyStaked();
        }

        bool isNative = currency == address(0);

        address guest = _msgSender();

        if (isNative) {
            if (msg.value != amount) {
                revert InvalidData();
            }
        } else {
            //-- transfer the ERC20 to the contract
            bool success = IERC20(currency).transferFrom(
                guest,
                address(this),
                amount
            );

            if (!success) {
                revert CannotTransfer();
            }
        }

        PaymentConfigRegistry registry = PaymentConfigRegistry(
            payable(configRegistry)
        );

        uint256 stakeAmount = (amount * 1000000) /
            (registry.feePPM() + 1000000);
        uint256 feeAmount = amount - stakeAmount;

        //-- transfer fee and notify
        _transfer(configRegistry, currency, feeAmount);
        registry.notifyFee(eventId, currency, feeAmount);

        _stake(stakeId, guest, vault, stakeAmount, currency);
    }

    function _stake(
        bytes32 stakeId,
        address guest,
        address vault,
        uint256 stakeAmount,
        address currency
    ) internal {
        bool isNative = currency == address(0);

        StakeVault stakeVault = StakeVault(payable(vault));

        if (!isNative) {
            //-- allow the vault to transfer the amount to itself
            IERC20(currency).approve(vault, stakeAmount);
        }

        stakeVault.stake{value: isNative ? stakeAmount : 0}(
            stakeId,
            currency,
            stakeAmount,
            guest
        );

        stakings[stakeId] = vault;
    }

    function getStakings(
        string[] calldata ids
    ) public view returns (Staking[] memory result) {
        uint256 length = ids.length;

        result = new Staking[](length);

        for (uint256 i = 0; i < length; ) {
            bytes32 stakeId = stringToId(ids[i]);
            address vault = stakings[stakeId];

            if (vault == address(0)) {
                result[i] = Staking(address(0), address(0), 0, 0, false, false);
            } else {
                StakeVault stakeVault = StakeVault(payable(vault));

                (
                    address guest,
                    address currency,
                    uint256 stakeAmount,
                    uint256 refundAmount,
                    bool slashed,
                    bool refunded
                ) = stakeVault.stakings(stakeId);

                result[i] = Staking(
                    guest,
                    currency,
                    stakeAmount,
                    refundAmount,
                    slashed,
                    refunded
                );
            }

            unchecked {
                ++i;
            }
        }

        return result;
    }

    function refund(
        string calldata paymentId,
        bytes calldata signature
    ) external {
        bytes32 stakeId = stringToId(paymentId);

        address vault = stakings[stakeId];

        if (vault == address(0)) {
            revert NoStaking();
        }

        //-- verify signature
        PaymentConfigRegistry registry = PaymentConfigRegistry(
            payable(configRegistry)
        );

        bytes32[] memory data = new bytes32[](2);

        data[0] = STAKE_REFUND;
        data[1] = stakeId;

        registry.assertSignature(data, signature);

        StakeVault stakeVault = StakeVault(payable(vault));

        stakeVault.refund(stakeId);
    }

    function slash(
        address vault,
        string[] memory paymentIds,
        bytes memory signature
    ) external {
        uint256 idsLength = paymentIds.length;

        PaymentConfigRegistry registry = PaymentConfigRegistry(
            payable(configRegistry)
        );

        //-- collect data to verify the signature
        bytes32[] memory data = new bytes32[](paymentIds.length + 1);
        bytes32[] memory stakeIds = new bytes32[](paymentIds.length);
        data[0] = STAKE_SLASH;

        for (uint256 i = 0; i < idsLength; ) {
            bytes32 stakeId = stringToId(paymentIds[i]);

            stakeIds[i] = stakeId;

            unchecked {
                ++i;
            }

            data[i] = stakeId;
        }

        registry.assertSignature(data, signature);

        StakeVault stakeVault = StakeVault(payable(vault));

        stakeVault.slash(stakeIds);
    }
}

contracts/payment/stake/StakeVault.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "../../utils/Vault.sol";

struct Staking {
    address guest;
    address currency;
    uint256 stakeAmount;
    uint256 refundAmount;
    bool slashed;
    bool refunded;
}

error NoStaking();
error AlreadyStaked();
error FundsNotAvailable();

bytes32 constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");

contract StakeVault is Vault {
    //-- STORAGE
    address public payoutAddress;
    uint256 public refundPPM; //-- key is setting id and value is refund PPM value
    mapping(bytes32 => Staking) public stakings; //-- key is stake id and value is staking info

    bytes32[] stakingIds;
    address[] currencies; //-- all the currency ever staked
    mapping(address => uint256) currencyIndex;
    bool inited;

    uint256[4] __gap;

    //-- ERRORS
    error InvalidData();
    error AccessDenied();

    constructor(address owner) {
        _grantRole(DEFAULT_ADMIN_ROLE, owner);
        _grantRole(OPERATOR_ROLE, _msgSender());
    }

    function initialize(
        address payout,
        uint256 ppm
    ) external onlyRole(OPERATOR_ROLE) {
        if (inited) {
            revert AccessDenied();
        }

        inited = true;

        payoutAddress = payout;
        _setRefundPPM(ppm);
    }

    function setPayoutAddress(
        address payout
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        payoutAddress = payout;
    }

    function setRefundPPM(uint256 ppm) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _setRefundPPM(ppm);
    }

    function stake(
        bytes32 stakeId,
        address currency,
        uint256 stakeAmount,
        address guest
    ) external payable onlyRole(OPERATOR_ROLE) {
        address sender = _msgSender();

        if (guest == address(0)) {
            revert InvalidData();
        }

        if (stakings[stakeId].guest != address(0)) {
            revert AlreadyStaked();
        }

        bool isNative = currency == address(0);

        //-- transfer the amount from caller to vault
        if (isNative) {
            if (msg.value != stakeAmount) {
                revert InvalidData();
            }
        } else {
            bool success = IERC20(currency).transferFrom(
                sender,
                address(this),
                stakeAmount
            );

            if (!success) {
                revert CannotTransfer();
            }
        }

        if (currencyIndex[currency] == 0) {
            uint256 index = currencies.length + 1;
            currencies.push(currency);
            currencyIndex[currency] = index;
        }

        uint256 refundAmount = (stakeAmount * refundPPM) / 1000000;

        Staking memory staking = Staking(
            guest,
            currency,
            stakeAmount,
            refundAmount,
            false,
            false
        );

        stakingIds.push(stakeId);
        stakings[stakeId] = staking;
    }

    function refund(bytes32 stakeId) external onlyRole(OPERATOR_ROLE) {
        Staking storage staking = stakings[stakeId];

        if (staking.guest == address(0)) {
            revert NoStaking();
        }

        staking.refunded = true;

        _transfer(staking.guest, staking.currency, staking.refundAmount);
    }

    function slash(
        bytes32[] calldata stakeIds
    ) external onlyRole(OPERATOR_ROLE) {
        uint256 idsLength = stakeIds.length;

        uint256 currenciesLength = currencies.length;
        uint256[] memory slashes = new uint256[](currenciesLength);

        for (uint256 i = 0; i < idsLength; ) {
            bytes32 stakeId = stakeIds[i];

            Staking storage staking = stakings[stakeId];

            if (staking.guest == address(0)) continue;

            if (staking.slashed || staking.refunded) {
                revert FundsNotAvailable();
            }

            //-- add to slash sum
            uint256 index = currencyIndex[staking.currency] - 1;
            slashes[index] += staking.stakeAmount;

            //-- update the staking
            staking.slashed = true;

            unchecked {
                ++i;
            }
        }

        //-- release
        for (uint256 i = 0; i < currenciesLength; ) {
            address currency = currencies[i];
            uint256 amount = slashes[currencyIndex[currency] - 1];

            if (amount > 0) {
                _transfer(payoutAddress, currency, amount);
            }

            unchecked {
                ++i;
            }
        }
    }

    function _setRefundPPM(uint256 ppm) internal {
        if (ppm > 1000000 || ppm == 0) {
            revert InvalidData();
        }

        refundPPM = ppm;
    }
}

